package mock;

import java.util.ArrayList;
import java.util.HashMap;

public class MoveToSamePosition {
	public static boolean judgeCircle(final String moves) {
		final HashMap<Character, Integer> moveMap = new HashMap<>();
		final ArrayList<Character> chars = new ArrayList<>();

		for (final char c : moves.toCharArray()) {
			moveMap.put(c, moveMap.getOrDefault(c, 0) + 1);
			if (!chars.contains(c)) {
				chars.add(c);
			}
		}

		final int up = moveMap.getOrDefault('U', 0);
		final int down = moveMap.getOrDefault('D', 0);
		final int left = moveMap.getOrDefault('L', 0);
		final int right = moveMap.getOrDefault('R', 0);

		return up == down && left == right;

	}

	public static void main(final String[] args) {
		System.out.println(judgeCircle(""));
	}

}
